import { Component } from "react";
import "../components/CssFile/NavBar.css"
import { GiHamburgerMenu } from "react-icons/gi";
import { IoMdClose } from "react-icons/io";

class Header extends Component {
  state = { clicked: false };
  // Hàm để thay đổi trạng thái clicked khi click vào biểu tượng menu
  handleClick = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  render() {
    return (
      <>
        <nav>


          <div className="group-menu">
            <ul id="Navbar" className={this.state.clicked ? "#Navbar active" : "#Navbar"}>
              <li><a href="#" className="Active">Home</a></li>
              <li><a href="#">About</a></li>
              <li><a href="#">Skill</a></li>
              <li><a href="#">Expensive</a></li>
              <li><a href="#">Contact</a></li>
            </ul>
          </div>
          <div id="Mobile" onClick={this.handleClick}>
            <i
              id="bar"
            >
            {this.state.clicked ? <IoMdClose  onClick={this.handleClick} /> : <GiHamburgerMenu onClick={this.handleClick}/>}
            </i>
           
          </div>


        </nav>
      </>
    );
  }
}

export default Header;
