import React from "react";
import "../components/CssFile/Card.css";

export default function ImageCard({ children, imgSrc, ...props }) {
    return (
        <div {...props} className="relative max-w-xs overflow-hidden">
            <img id="card" src="https://th.bing.com/th/id/OIP.SUCA0pzUDgMOEv7_pFLL0AAAAA?rs=1&pid=ImgDetMain" alt="" className="transition-transform hover:scale-1 duration-200"></img>
            <div className="absolute inset-0  flex items-end bg-gradient-to-t from-black/60 to-transparent">
                <div className="p-4 text-white">{children}</div>
            </div>



        </div>
    );
}
