import React from "react";
import { RiReactjsLine } from "react-icons/ri";
import { TbBrandNextjs } from "react-icons/tb";
import { SiMongodb } from "react-icons/si";
import { DiRedis } from "react-icons/di";
import { FaNodeJs } from "react-icons/fa";
import { BiLogoPostgresql } from "react-icons/bi";
import { animate, motion } from "framer-motion";
import { FaJava } from "react-icons/fa";
import { FaSwift } from "react-icons/fa";
import "../components/CssFile/Technologies.css"

const iconVariants = (duration) => ({
    initial: { y: -10 },
    animate: {
        y: [10, -10],
        transition: {
            duration: duration,
            ease: "linear",
            repeat: Infinity,
            repeatType: "reverse",
        },
    },
});


const Technologies = () => {
    return (
        <div className="borderr-b border-neutral-800 pb-24">
            <motion.h1
            whileInView={{opacity:1, x:0}}
            initial={{opacity:0,x:-100}}
            transition={{duration:1.5}}
            className="my-20 text-center text-4xl">Skills</motion.h1>
            <h2 className="title_tech">Languages</h2>
            <div
            whileInView={{opacity:1, x:0}}
            initial={{opacity:0,x:-100}}
            transition={{duration:1.5}}
            className="flex flex-wrap items-center justify-center gap-4">
                <div
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <RiReactjsLine className="text-7xl text-cyan-400" />
                </div>
                <div 
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <FaJava className="text-7xl" />
                </div>
                <div
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <FaSwift className="text-7xl text-white-500" />
                </div>
                <div
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <DiRedis className="text-7xl text-red-700" />
                </div>
                <div
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <FaNodeJs className="text-7xl text-green-500" />
                </div>
                <div
                variants={iconVariants(4)}
                initial="initial"
                animate="animate"
                className="rounded-2xl border-4 border-neutral-800 p-4">
                    <BiLogoPostgresql className="text-7xl text-sky-700" />
                </div>
            </div>
           
        </div>
        
    );
}

export default Technologies;
